import React from "react";
import { View, Text, TouchableOpacity, Image } from "react-native";
import logoImg from "../../assets/logo.png";
import Style from "./style";
import { WebView } from 'react-native-webview';
import api from '../../services/api';

export default function Logica({ navigation }) {
  const [subjects, setSubjects] = useState([]);

    useEffect(() => {
        (async function getSubjects() {
            const subjectsDatas = (await Api.get('/subjects')).data;
            const subjectsArray = [];

            for(let index = 0; index < subjectsDatas.length; index++) {
                const subject = subjectsDatas[index];

                subjectsArray.push({
                    id: subject.id.toString(),
                    name: subject.name
                });
            }

            setSubjects(subjectsArray);
        })();
    }, []);
  return (
    <View style={Style.container}>
      <View style={Style.header}>
        <View style={Style.header}>
          <Image source={logoImg} />
        </View>
        <Text style={Style.title}>Lógica de Programação</Text>

         <View style={Style.body}>
            <View style={Style.player}>
                <WebView source={{ uri: 'https://www.youtube.com/watch?v=VUkOQNTul1I'}} allowsFullscreenVideo={true} />
                
            </View>            
        </View>

      </View>
    </View>
  );
}
